export const initialState = {
  doctors: [],
  departments: [],
  selectedDepartment: '',
  selectedDoctorId: '',
  selectedDoctor: null,
  availableSlots: [],
  selectedDate: '',
  selectedTime: '',
  comments: '',
  reportFile: null,
};

export function appointmentReducer(state, action) {
  switch (action.type) {
    case 'SET_DOCTORS':
      return {
        ...state,
        doctors: action.payload,
        departments: [...new Set(action.payload.map(doc => doc.department))],
      };
    case 'SET_DEPARTMENT':
      return {
        ...state,
        selectedDepartment: action.payload,
        selectedDoctorId: '',
        selectedDate: '',
        selectedTime: '',
        selectedDoctor: null,
        availableSlots: [],
      };
    case 'SET_DOCTOR_ID':
      return {
        ...state,
        selectedDoctorId: action.payload,
        selectedDate: '',
        selectedTime: '',
      };
    case 'SET_SELECTED_DOCTOR':
      return {
        ...state,
        selectedDoctor: action.payload,
      };
    case 'SET_AVAILABLE_SLOTS':
      return {
        ...state,
        availableSlots: action.payload,
      };
    case 'SET_DATE':
      return {
        ...state,
        selectedDate: action.payload,
        selectedTime: '',
      };
    case 'SET_TIME':
      return {
        ...state,
        selectedTime: action.payload,
      };
    case 'SET_COMMENTS':
      return {
        ...state,
        comments: action.payload,
      };
    case 'SET_REPORT':
      return {
        ...state,
        reportFile: action.payload,
      };
    case 'RESET_FORM':
      return initialState;
    default:
      return state;
  }
}
